cmd:
cat << EOF > /etc/nginx/nginx.conf && exec nginx -g 'daemon off;'
worker_processes 1;
events {
    worker_connections 1024;
}
http {
    server {
        listen 80;
        resolver $(echo $(cat /etc/resolv.conf | grep ^nameserver\  | cut -d ' ' -f 2));
        location / {
            proxy_pass http://\$host.${GROUP:-external}.marathon.l4lb.thisdcos.directory\$request_uri;
        }
    }
}
